        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        // Resolve the promise with the retrieved data
        resolve(data);
      })
      .catch(error => {
        // Reject the promise with the encountered error
        reject(error);
      });
  });
}
